package org.logical.arisGlobal;

public class EvenOddWithoutArithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number = 11;
		int num = number & 1;
		if(num == 0){
			System.out.println("Even number");
		}else{
			System.out.println("Odd number");
		}
		
	}

}
